import React from 'react';
import { useRouter } from 'next/router'
import Layout from '../../components/Layout';
import { useQuery, gql, useMutation } from '@apollo/client'
import { Formik } from 'formik'
import * as Yup from 'yup'
import Swal from 'sweetalert2';

const OBTENER_CLIENTE = gql`
    query obtenerCliente($id:ID!) {
        obtenerCliente(id:$id) {
            nombre
            apellido
            direccion
            ciudad
            pais
            email
            telefono
            empresa
        }
    }
`;

const ACTUALIZAR_CLIENTE = gql`
    mutation actualizarCliente($id: ID!, $input: ClienteInput) {
        actualizarCliente(id: $id, input: $input) {
            nombre
            email
        }
    }
`;

const EditarCliente = () => {
    // obtener el ID actual
    const router = useRouter();
    const { query: { id } } = router;
    // console.log(id)

    // Consultar para obtener el cliente
    const { data, loading, error } = useQuery(OBTENER_CLIENTE, {
        variables: {
            id
        }
    });

    // Actualizar el cliente
    const [actualizarCliente] = useMutation(ACTUALIZAR_CLIENTE);
    const phoneRegex = RegExp(
        /^\(?([0-9]{3})\)?[-. ]?([0-9]{3})[-. ]?([0-9]{4})$/
    );

    // Schema de validacion
    const schemaValidacion = Yup.object({
        nombre: Yup.string()
            .required('El nombre del cliente es obligatorio'),
        apellido: Yup.string()
            .required('El apellido del cliente es obligatorio'),
        empresa: Yup.string()
            .required('El campo empresa  es obligatorio'),
        email: Yup.string()
            .email('Email no válido')
            .required('El email del cliente es obligatorio'),
        direccion: Yup.string()
            .required('La direccion del cliente es obligatorio'),
        ciudad: Yup.string()
            .required('La direccion del cliente es obligatorio'),
        pais: Yup.string()
            .required('La direccion del cliente es obligatorio'),
        pais: Yup.string()
            .required('El pais del cliente es obligatorio'),
        telefono: Yup.string().matches(phoneRegex, "Número invalido").required("Telefono es obligatorio")
    });


    if (loading) return 'Cargando...';

    // console.log(data.obtenerCliente)

    const { obtenerCliente } = data;

    // Modifica el cliente en la BD
    const actualizarInfoCliente = async valores => {
        const { nombre, apellido, direccion, ciudad, pais, empresa, email, telefono } = valores;

        try {
            const { data } = await actualizarCliente({
                variables: {
                    id,
                    input: {
                        nombre,
                        apellido,
                        direccion,
                        ciudad,
                        pais,
                        empresa,
                        email,
                        telefono
                    }
                }
            });

            // console.log(data);

            // Mostrar Alerta
            Swal.fire(
                'Actualizado',
                'El cliente se actualizó correctamente',
                'success'
            )

            // Redireccionar
            router.push('/');
        } catch (error) {
            console.log(error);
        }
    }

    return (
        <Layout>
            <h1 className="text-2xl text-gray-800 font-light">Editar Cliente</h1>

            <div className="flex justify-center mt-5">
                <div className="w-full max-w-lg">

                    <Formik
                        validationSchema={schemaValidacion}
                        enableReinitialize
                        initialValues={obtenerCliente}
                        onSubmit={(valores) => {
                            actualizarInfoCliente(valores)
                        }}
                    >

                        {props => {
                            // console.log(props);
                            return (
                                <form
                                    className="bg-white shadow-md px-8 pt-6 pb-8 mb-4"
                                    onSubmit={props.handleSubmit}
                                >
                                    <div className="mb-4">
